    def changenum_mobile(self, change):
        self.num = change
        self.lcdNumber.display(self.num)
        _translate = QtCore.QCoreApplication.translate
        self.open_kb.setText(_translate("Form", str(self.num)))
    def goup(self):
        try:
            self.changenum_mobile(self.num + 1)
        except:
            os.system("zenity --warning --text=\"Reaced Number Limit\"")
    def godown(self):
        try:
            self.changenum_mobile(self.num - 1)
        except:
            os.system("zenity --warning --text=\"Reaced Number Limit\"")
    def kA(self):
        try: self.changenum_mobile((self.num * 10))
        except:
            os.system("zenity --warning --text=\"Reaced Number Limit\"")
    def kB(self):
        try: self.changenum_mobile((self.num * 10)+1)
        except:
            os.system("zenity --warning --text=\"Reaced Number Limit\"")
    def bk(self):
        if len(str(self.num)) > 1:
            try: self.changenum_mobile(int(str(self.num)[1:]))
            except:
                os.system("zenity --warning --text=\"Reaced Number Limit\"")
    def bC(self):
        try: self.changenum_mobile((self.num * 10)+2)
        except:
            os.system("zenity --warning --text=\"Reaced Number Limit\"")
    def bD(self):
        try: self.changenum_mobile((self.num * 10)+3)
        except:
            os.system("zenity --warning --text=\"Reaced Number Limit\"")
