    def bI(self):
        try: self.changenum_mobile((self.num * 10)+8)
        except:
            os.system("zenity --warning --text=\"Reaced Number Limit\"")
    def bJ(self):
        try: self.changenum_mobile((self.num * 10)+9)
        except:
            os.system("zenity --warning --text=\"Reaced Number Limit\"")
